import React, { useState, ChangeEvent, FormEvent } from 'react';
import { Line } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

type Workout = {
    exercise: string;
    sets: number;
    reps: number;
    weight: number;
    date: string;
    notes: string;
};

const TrackingSystem: React.FC = () => {
    const [workouts, setWorkouts] = useState<Workout[]>([]);
    const [exercise, setExercise] = useState<string>('');
    const [sets, setSets] = useState<string>('');
    const [reps, setReps] = useState<string>('');
    const [weight, setWeight] = useState<string>('');
    const [date, setDate] = useState<string>('');
    const [notes, setNotes] = useState<string>('');
    const [tab, setTab] = useState<'entry' | 'stats'>('entry');

    const handleAddWorkout = () => {
        const newWorkout: Workout = {
            exercise,
            sets: parseInt(sets),
            reps: parseInt(reps),
            weight: parseFloat(weight),
            date: new Date(date).toLocaleDateString(),
            notes,
        };
        setWorkouts([...workouts, newWorkout]);
        resetForm();
    };

    const resetForm = () => {
        setExercise('');
        setSets('');
        setReps('');
        setWeight('');
        setDate('');
        setNotes('');
    };

    // CSV Export
    const exportCSV = () => {
        const header = 'Datum,Übung,Gewicht,Sätze,Wdh.,Notizen\n';
        const csv =
            header +
            workouts
                .map(
                    (w) => `${w.date},${w.exercise},${w.weight},${w.sets},${w.reps},${w.notes}`
                )
                .join('\n');
        const blob = new Blob([csv], { type: 'text/csv' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = 'trainingsdaten.csv';
        a.click();
        URL.revokeObjectURL(url);
    };

    return (
        <div style={{
            maxWidth: 700,
            margin: '2rem auto',
            padding: '2rem',
            background: '#181c24',
            borderRadius: '1rem',
            boxShadow: '0 2px 16px rgba(0,0,0,0.18)',
            fontFamily: 'Segoe UI, Arial, sans-serif',
            color: '#f5f5f5'
        }}>
            <div style={{display: 'flex', gap: '1rem', marginBottom: '2rem'}}>
                <button onClick={() => setTab('entry')} style={{flex: 1, padding: '0.75rem', background: tab==='entry' ? '#1976d2' : '#23283a', color: tab==='entry' ? 'white' : '#f5f5f5', border: 'none', borderRadius: '0.5rem', fontWeight: 600, cursor: 'pointer'}}>Trainingseintrag</button>
                <button onClick={() => setTab('stats')} style={{flex: 1, padding: '0.75rem', background: tab==='stats' ? '#1976d2' : '#23283a', color: tab==='stats' ? 'white' : '#f5f5f5', border: 'none', borderRadius: '0.5rem', fontWeight: 600, cursor: 'pointer'}}>Auswertung</button>
            </div>
            {tab === 'entry' && (
                <>
                    <h2 style={{textAlign: 'center', color: '#1976d2'}}>Workout Tracking</h2>
                    <div style={{marginBottom: '2rem', background: '#23283a', padding: '1rem', borderRadius: '0.5rem'}}>
                        <h3 style={{color: '#43a047'}}>Trainingsplan für 18 Wochen (Right Guard, American Football)</h3>
                        <p><strong>3x pro Woche, je max. 2h</strong></p>
                        <ol style={{paddingLeft: '1.2rem', color: '#f5f5f5'}}>
                            <li><strong>Woche 1-4 (Wiedereinstieg):</strong>
                                <ul>
                                    <li>Tag 1: Kniebeugen, Bankdrücken, Rudern, Planks</li>
                                    <li>Tag 2: Kettlebell Swings, Box Jumps, Medizinballwürfe, Core</li>
                                    <li>Tag 3: Schulterdrücken, Klimmzüge (Assist), Push-Ups, Mobility</li>
                                </ul>
                            </li>
                            <li><strong>Woche 5-8 (Steigerung):</strong>
                                <ul>
                                    <li>Tag 1: Kniebeugen, Kreuzheben, Ausfallschritte, Farmer's Walk</li>
                                    <li>Tag 2: Bankdrücken, Schulterdrücken, Rudern, Trizeps</li>
                                    <li>Tag 3: Sprungübungen, Medizinball, Agility Ladder, Core</li>
                                </ul>
                            </li>
                            <li><strong>Woche 9-12 (Kraft & Speed):</strong>
                                <ul>
                                    <li>Tag 1: Kniebeugen, Power Cleans, Planks, Mobility</li>
                                    <li>Tag 2: Bankdrücken, Klimmzüge, Push-Ups, Trizeps</li>
                                    <li>Tag 3: Explosivität (Box Jumps, Medizinballwürfe), Agility, Core</li>
                                </ul>
                            </li>
                            <li><strong>Woche 13-16 (Intensivierung):</strong>
                                <ul>
                                    <li>Tag 1: Kniebeugen, Kreuzheben, Farmer's Walk, Planks</li>
                                    <li>Tag 2: Bankdrücken, Schulterdrücken, Rudern, Mobility</li>
                                    <li>Tag 3: Speed & Plyos, Agility Ladder, Core</li>
                                </ul>
                            </li>
                            <li><strong>Woche 17-18 (Peak & Saisonvorbereitung):</strong>
                                <ul>
                                    <li>Tag 1: Kniebeugen, Power Cleans, Planks</li>
                                    <li>Tag 2: Bankdrücken, Klimmzüge, Push-Ups</li>
                                    <li>Tag 3: Explosivität, Mobility, Stretching</li>
                                </ul>
                            </li>
                        </ol>
                        <p style={{fontSize: '0.95rem', color: '#bbb'}}>
                            <strong>Empfehlung:</strong> Starte mit moderaten Gewichten, Fokus auf Technik und saubere Ausführung. Steigere dich jede Woche leicht.<br />
                            Ab Woche 5 Intensität und Gewicht langsam erhöhen, Speed- und Kraftübungen ausbauen.<br />
                            In Woche 17-18: Fokus auf Explosivität, Mobilität und Regeneration.
                        </p>
                    </div>
                    <form
                        onSubmit={(e: FormEvent<HTMLFormElement>) => {
                            e.preventDefault();
                            handleAddWorkout();
                        }}
                        style={{display: 'grid', gap: '1rem', marginBottom: '2rem'}}
                    >
                        <div style={{display: 'grid', gap: '0.5rem'}}>
                            <label htmlFor="exercise" style={{fontWeight: 500}}>Übung</label>
                            <input
                                id="exercise"
                                type="text"
                                placeholder="Exercise"
                                value={exercise}
                                onChange={(e: ChangeEvent<HTMLInputElement>) => setExercise(e.target.value)}
                                required
                                style={{padding: '0.5rem', borderRadius: '0.5rem', border: '1px solid #ccc'}}
                            />
                        </div>
                        <div style={{display: 'grid', gap: '0.5rem'}}>
                            <label htmlFor="weight" style={{fontWeight: 500}}>Gewicht (kg)</label>
                            <input
                                id="weight"
                                type="number"
                                placeholder="Gewicht"
                                value={weight}
                                onChange={(e: ChangeEvent<HTMLInputElement>) => setWeight(e.target.value)}
                                required
                                style={{padding: '0.5rem', borderRadius: '0.5rem', border: '1px solid #ccc'}}
                            />
                        </div>
                        <div style={{display: 'grid', gap: '0.5rem'}}>
                            <label htmlFor="sets" style={{fontWeight: 500}}>Sätze</label>
                            <input
                                id="sets"
                                type="number"
                                placeholder="Sets"
                                value={sets}
                                onChange={(e: ChangeEvent<HTMLInputElement>) => setSets(e.target.value)}
                                required
                                style={{padding: '0.5rem', borderRadius: '0.5rem', border: '1px solid #ccc'}}
                            />
                        </div>
                        <div style={{display: 'grid', gap: '0.5rem'}}>
                            <label htmlFor="reps" style={{fontWeight: 500}}>Wiederholungen</label>
                            <input
                                id="reps"
                                type="number"
                                placeholder="Reps"
                                value={reps}
                                onChange={(e: ChangeEvent<HTMLInputElement>) => setReps(e.target.value)}
                                required
                                style={{padding: '0.5rem', borderRadius: '0.5rem', border: '1px solid #ccc'}}
                            />
                        </div>
                        <div style={{display: 'grid', gap: '0.5rem'}}>
                            <label htmlFor="date" style={{fontWeight: 500}}>Datum</label>
                            <input
                                id="date"
                                type="date"
                                value={date}
                                onChange={(e: ChangeEvent<HTMLInputElement>) => setDate(e.target.value)}
                                required
                                style={{padding: '0.5rem', borderRadius: '0.5rem', border: '1px solid #ccc'}}
                            />
                        </div>
                        <div style={{display: 'grid', gap: '0.5rem'}}>
                            <label htmlFor="notes" style={{fontWeight: 500}}>Notizen</label>
                            <input
                                id="notes"
                                type="text"
                                placeholder="Notizen"
                                value={notes}
                                onChange={(e: ChangeEvent<HTMLInputElement>) => setNotes(e.target.value)}
                                style={{padding: '0.5rem', borderRadius: '0.5rem', border: '1px solid #ccc'}}
                            />
                        </div>
                        <button type="submit" style={{padding: '0.75rem', background: '#1976d2', color: 'white', border: 'none', borderRadius: '0.5rem', fontWeight: 600, cursor: 'pointer'}}>Workout hinzufügen</button>
                    </form>
                    <h3 style={{marginBottom: '1rem', color: '#1976d2'}}>Deine Einträge</h3>
                    <ul style={{listStyle: 'none', padding: 0, marginBottom: '2rem'}}>
                        {workouts.map((workout, index) => (
                            <li key={index} style={{background: '#f5f5f5', marginBottom: '0.5rem', padding: '0.75rem', borderRadius: '0.5rem'}}>
                                <strong>{workout.date}</strong>: {workout.exercise} – {workout.weight} kg, {workout.sets} Sätze x {workout.reps} Wdh. {workout.notes && <>| <em>{workout.notes}</em></>}
                            </li>
                        ))}
                    </ul>
                    <button onClick={exportCSV} style={{padding: '0.75rem', background: '#43a047', color: 'white', border: 'none', borderRadius: '0.5rem', fontWeight: 600, cursor: 'pointer'}}>Export als CSV</button>
                </>
            )}
            {tab === 'stats' && (
                <>
                    <h2 style={{textAlign: 'center', color: '#1976d2'}}>Auswertung</h2>
                    <div style={{background: '#23283a', padding: '1rem', borderRadius: '0.5rem', marginBottom: '2rem'}}>
                        <p><strong>Trainingsanzahl:</strong> {workouts.length}</p>
                        <p><strong>Gesamtgewicht bewegt:</strong> {workouts.reduce((sum, w) => sum + (w.weight * w.sets * w.reps), 0)} kg</p>
                        <p><strong>Durchschnittliches Gewicht pro Satz:</strong> {workouts.length > 0 ? (workouts.reduce((sum, w) => sum + w.weight, 0) / workouts.length).toFixed(2) : 0} kg</p>
                        <p><strong>Letztes Training:</strong> {workouts.length > 0 ? workouts[workouts.length-1].date : '-'}</p>
                    </div>
                    <div style={{background: '#23283a', padding: '1rem', borderRadius: '0.5rem', marginBottom: '2rem'}}>
                        <h3 style={{color: '#f5f5f5'}}>Gewichtsentwicklung</h3>
                        <Line
                            data={{
                                labels: workouts.map(w => w.date),
                                datasets: [
                                    {
                                        label: 'Gewicht (kg)',
                                        data: workouts.map(w => w.weight),
                                        borderColor: '#43a047',
                                        backgroundColor: 'rgba(67,160,71,0.2)',
                                        tension: 0.3,
                                    },
                                ],
                            }}
                            options={{
                                responsive: true,
                                plugins: {
                                    legend: {
                                        labels: { color: '#f5f5f5' }
                                    },
                                },
                                scales: {
                                    x: {
                                        ticks: { color: '#f5f5f5' },
                                        grid: { color: '#333' }
                                    },
                                    y: {
                                        ticks: { color: '#f5f5f5' },
                                        grid: { color: '#333' }
                                    }
                                }
                            }}
                        />
                    </div>
                    <h3 style={{marginBottom: '1rem', color: '#1976d2'}}>Alle Einträge</h3>
                    <ul style={{listStyle: 'none', padding: 0}}>
                        {workouts.map((workout, index) => (
                            <li key={index} style={{background: '#23283a', marginBottom: '0.5rem', padding: '0.75rem', borderRadius: '0.5rem', color: '#f5f5f5'}}>
                                <strong>{workout.date}</strong>: {workout.exercise} – {workout.weight} kg, {workout.sets} Sätze x {workout.reps} Wdh. {workout.notes && <>| <em>{workout.notes}</em></>}
                            </li>
                        ))}
                    </ul>
                </>
            )}
        </div>
    );
};

export default TrackingSystem;
