import React, { useState, ChangeEvent, FormEvent } from 'react';
import './TrackingSystem.css';
import { Line } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

type Workout = {
    exercise: string;
    sets: number;
    reps: number;
    weight: number;
    date: string;
    notes: string;
};

const TrackingSystem: React.FC = () => {
    const [workouts, setWorkouts] = useState<Workout[]>([]);
    const [exercise, setExercise] = useState<string>('');
    const [sets, setSets] = useState<string>('');
    const [reps, setReps] = useState<string>('');
    const [weight, setWeight] = useState<string>('');
    // Datum wird automatisch gesetzt
    const [notes, setNotes] = useState<string>('');
    const [tab, setTab] = useState<'entry' | 'stats'>('entry');

    const handleAddWorkout = () => {
        const newWorkout: Workout = {
            exercise,
            sets: parseInt(sets),
            reps: parseInt(reps),
            weight: parseFloat(weight),
            date: new Date().toLocaleDateString(),
            notes,
        };
        setWorkouts([...workouts, newWorkout]);
        resetForm();
    };

    const resetForm = () => {
        setExercise('');
        setSets('');
        setReps('');
        setWeight('');
        setNotes('');
    };

    // CSV Export
    const exportCSV = () => {
        const header = 'Datum,Übung,Gewicht,Sätze,Wdh.,Notizen\n';
        const csv =
            header +
            workouts
                .map(
                    (w) => `${w.date},${w.exercise},${w.weight},${w.sets},${w.reps},${w.notes}`
                )
                .join('\n');
        const blob = new Blob([csv], { type: 'text/csv' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = 'trainingsdaten.csv';
        a.click();
        URL.revokeObjectURL(url);
    };

    // Workout löschen
    const handleDeleteWorkout = (index: number) => {
        setWorkouts(workouts.filter((_, i) => i !== index));
    };

    return (
    <div className="tracking-main">
            <div className="tab-bar">
                <button onClick={() => setTab('entry')} className={tab==='entry' ? 'tab-btn active' : 'tab-btn'}>Trainingseintrag</button>
                <button onClick={() => setTab('stats')} className={tab==='stats' ? 'tab-btn active' : 'tab-btn'}>Auswertung</button>
            </div>
            {tab === 'entry' && (
                <>
                    <h2 className="heading-entries center">Workout Tracking</h2>
                    <div className="tracking-container">
                        <h3 className="heading-exercise">Trainingsplan für 18 Wochen (Right Guard, American Football)</h3>
                        <p><strong>3x pro Woche, je max. 2h</strong></p>
                        <ol className="plan-list">
                            <li><strong>Woche 1-4 (Wiedereinstieg):</strong>
                                <ul>
                                    <li>Tag 1: Kniebeugen, Bankdrücken, Rudern, Planks</li>
                                    <li>Tag 2: Kettlebell Swings, Box Jumps, Medizinballwürfe, Core</li>
                                    <li>Tag 3: Schulterdrücken, Klimmzüge (Assist), Push-Ups, Mobility</li>
                                </ul>
                            </li>
                            <li><strong>Woche 5-8 (Steigerung):</strong>
                                <ul>
                                    <li>Tag 1: Kniebeugen, Kreuzheben, Ausfallschritte, Farmer's Walk</li>
                                    <li>Tag 2: Bankdrücken, Schulterdrücken, Rudern, Trizeps</li>
                                    <li>Tag 3: Sprungübungen, Medizinball, Agility Ladder, Core</li>
                                </ul>
                            </li>
                            <li><strong>Woche 9-12 (Kraft & Speed):</strong>
                                <ul>
                                    <li>Tag 1: Kniebeugen, Power Cleans, Planks, Mobility</li>
                                    <li>Tag 2: Bankdrücken, Klimmzüge, Push-Ups, Trizeps</li>
                                    <li>Tag 3: Explosivität (Box Jumps, Medizinballwürfe), Agility, Core</li>
                                </ul>
                            </li>
                            <li><strong>Woche 13-16 (Intensivierung):</strong>
                                <ul>
                                    <li>Tag 1: Kniebeugen, Kreuzheben, Farmer's Walk, Planks</li>
                                    <li>Tag 2: Bankdrücken, Schulterdrücken, Rudern, Mobility</li>
                                    <li>Tag 3: Speed & Plyos, Agility Ladder, Core</li>
                                </ul>
                            </li>
                            <li><strong>Woche 17-18 (Peak & Saisonvorbereitung):</strong>
                                <ul>
                                    <li>Tag 1: Kniebeugen, Power Cleans, Planks</li>
                                    <li>Tag 2: Bankdrücken, Klimmzüge, Push-Ups</li>
                                    <li>Tag 3: Explosivität, Mobility, Stretching</li>
                                </ul>
                            </li>
                        </ol>
                        <p className="plan-note">
                            <strong>Empfehlung:</strong> Starte mit moderaten Gewichten, Fokus auf Technik und saubere Ausführung. Steigere dich jede Woche leicht.<br />
                            Ab Woche 5 Intensität und Gewicht langsam erhöhen, Speed- und Kraftübungen ausbauen.<br />
                            In Woche 17-18: Fokus auf Explosivität, Mobilität und Regeneration.
                        </p>
                    </div>
                    <form
                        onSubmit={(e: FormEvent<HTMLFormElement>) => {
                            e.preventDefault();
                            handleAddWorkout();
                        }}
                        className="form-grid"
                    >
                        <div className="form-group">
                            <label htmlFor="exercise">Übung</label>
                            <input
                                id="exercise"
                                type="text"
                                placeholder="Exercise"
                                value={exercise}
                                onChange={(e: ChangeEvent<HTMLInputElement>) => setExercise(e.target.value)}
                                required
                                className="input-field"
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="weight">Gewicht (kg)</label>
                            <input
                                id="weight"
                                type="number"
                                placeholder="Gewicht"
                                value={weight}
                                onChange={(e: ChangeEvent<HTMLInputElement>) => setWeight(e.target.value)}
                                required
                                className="input-field"
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="sets">Sätze</label>
                            <input
                                id="sets"
                                type="number"
                                placeholder="Sets"
                                value={sets}
                                onChange={(e: ChangeEvent<HTMLInputElement>) => setSets(e.target.value)}
                                required
                                className="input-field"
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="reps">Wiederholungen</label>
                            <input
                                id="reps"
                                type="number"
                                placeholder="Reps"
                                value={reps}
                                onChange={(e: ChangeEvent<HTMLInputElement>) => setReps(e.target.value)}
                                required
                                className="input-field"
                            />
                        </div>
                        {/* Datum wird automatisch gesetzt */}
                        <div className="form-group">
                            <label htmlFor="notes">Notizen</label>
                            <input
                                id="notes"
                                type="text"
                                placeholder="Notizen"
                                value={notes}
                                onChange={(e: ChangeEvent<HTMLInputElement>) => setNotes(e.target.value)}
                                className="input-field"
                            />
                        </div>
                        <button type="submit" className="tab-btn active">Workout hinzufügen</button>
                    </form>
                    <h3 className="heading-entries">Deine Einträge</h3>
                    <ul className="entries-list">
                        {workouts.map((workout, index) => (
                            <li key={index} className="entry-item light">
                                <span>
                                    <strong>{workout.date}</strong>: {workout.exercise} – {workout.weight} kg, {workout.sets} Sätze x {workout.reps} Wdh. {workout.notes && <>| <em>{workout.notes}</em></>}
                                </span>
                                <button onClick={() => handleDeleteWorkout(index)} className="delete-btn" aria-label="Eintrag löschen">Löschen</button>
                            </li>
                        ))}
                    </ul>
                    <button onClick={exportCSV} className="export-btn">Export als CSV</button>
                </>
            )}
            {tab === 'stats' && (
                <>
                    <h2 className="heading-entries center">Auswertung</h2>
                    <div className="tracking-container">
                        <p><strong>Trainingsanzahl:</strong> {workouts.length}</p>
                        <p><strong>Gesamtgewicht bewegt:</strong> {workouts.reduce((sum, w) => sum + (w.weight * w.sets * w.reps), 0)} kg</p>
                        <p><strong>Durchschnittliches Gewicht pro Satz:</strong> {workouts.length > 0 ? (workouts.reduce((sum, w) => sum + w.weight, 0) / workouts.length).toFixed(2) : 0} kg</p>
                        <p><strong>Letztes Training:</strong> {workouts.length > 0 ? workouts[workouts.length-1].date : '-'}</p>
                    </div>
                    <div className="tracking-container">
                        <h3 className="heading-main">Gewichtsentwicklung (alle Einträge)</h3>
                        <Line
                            data={{
                                labels: workouts.map(w => w.date),
                                datasets: [
                                    {
                                        label: 'Gewicht (kg)',
                                        data: workouts.map(w => w.weight),
                                        borderColor: '#43a047',
                                        backgroundColor: 'rgba(67,160,71,0.2)',
                                        tension: 0.3,
                                    },
                                ],
                            }}
                            options={{
                                responsive: true,
                                plugins: {
                                    legend: {
                                        labels: { color: '#f5f5f5' }
                                    },
                                },
                                scales: {
                                    x: {
                                        ticks: { color: '#f5f5f5' },
                                        grid: { color: '#333' }
                                    },
                                    y: {
                                        ticks: { color: '#f5f5f5' },
                                        grid: { color: '#333' }
                                    }
                                }
                            }}
                        />
                        <h3 className="heading-main" style={{marginTop: '2rem'}}>Auswertung pro Übung</h3>
                        {Array.from(new Set(workouts.map(w => w.exercise))).map((exerciseName) => {
                            const exerciseData = workouts.filter(w => w.exercise === exerciseName);
                            return (
                                <div key={exerciseName} className="tracking-container">
                                    <h4 className="heading-exercise">{exerciseName}</h4>
                                    <Line
                                        data={{
                                            labels: exerciseData.map(w => w.date),
                                            datasets: [
                                                {
                                                    label: `${exerciseName} Gewicht (kg)`,
                                                    data: exerciseData.map(w => w.weight),
                                                    borderColor: '#1976d2',
                                                    backgroundColor: 'rgba(25,118,210,0.2)',
                                                    tension: 0.3,
                                                },
                                            ],
                                        }}
                                        options={{
                                            responsive: true,
                                            plugins: {
                                                legend: {
                                                    labels: { color: '#f5f5f5' }
                                                },
                                            },
                                            scales: {
                                                x: {
                                                    ticks: { color: '#f5f5f5' },
                                                    grid: { color: '#333' }
                                                },
                                                y: {
                                                    ticks: { color: '#f5f5f5' },
                                                    grid: { color: '#333' }
                                                }
                                            }
                                        }}
                                    />
                                </div>
                            );
                        })}
                    </div>
                    <h3 className="heading-entries">Alle Einträge</h3>
                    <ul className="entries-list">
                        {workouts.map((workout, index) => (
                            <li key={index} className="entry-item">
                                <span>
                                    <strong>{workout.date}</strong>: {workout.exercise} – {workout.weight} kg, {workout.sets} Sätze x {workout.reps} Wdh. {workout.notes && <>| <em>{workout.notes}</em></>}
                                </span>
                                <button onClick={() => handleDeleteWorkout(index)} className="delete-btn" aria-label="Eintrag löschen">Löschen</button>
                            </li>
                        ))}
                    </ul>
                </>
            )}
        </div>
    );
};

export default TrackingSystem;
