import React, { useState, ChangeEvent, FormEvent } from 'react';

type Workout = {
    exercise: string;
    sets: number;
    reps: number;
    weight: number;
    date: string;
    notes: string;
};

const TrackingSystem: React.FC = () => {
    const [workouts, setWorkouts] = useState<Workout[]>([]);
    const [exercise, setExercise] = useState<string>('');
    const [sets, setSets] = useState<string>('');
    const [reps, setReps] = useState<string>('');
    const [weight, setWeight] = useState<string>('');
    const [date, setDate] = useState<string>('');
    const [notes, setNotes] = useState<string>('');
    const [tab, setTab] = useState<'entry' | 'stats'>('entry');

    const handleAddWorkout = () => {
        const newWorkout: Workout = {
            exercise,
            sets: parseInt(sets),
            reps: parseInt(reps),
            weight: parseFloat(weight),
            date: new Date(date).toLocaleDateString(),
            notes,
        };
        setWorkouts([...workouts, newWorkout]);
        resetForm();
    };

    const resetForm = () => {
        setExercise('');
        setSets('');
        setReps('');
        setWeight('');
        setDate('');
        setNotes('');
    };

    // CSV Export
    const exportCSV = () => {
        const header = 'Datum,Übung,Gewicht,Sätze,Wdh.,Notizen\n';
        const csv =
            header +
            workouts
                .map(
                    (w) => `${w.date},${w.exercise},${w.weight},${w.sets},${w.reps},${w.notes}`
                )
                .join('\n');
        const blob = new Blob([csv], { type: 'text/csv' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = 'trainingsdaten.csv';
        a.click();
        URL.revokeObjectURL(url);
    };

    return (
        <div style={{
            maxWidth: 600,
            margin: '2rem auto',
            padding: '2rem',
            background: 'white',
            borderRadius: '1rem',
            boxShadow: '0 2px 16px rgba(0,0,0,0.08)',
            fontFamily: 'Segoe UI, Arial, sans-serif'
        }}>
            <div style={{display: 'flex', gap: '1rem', marginBottom: '2rem'}}>
                <button onClick={() => setTab('entry')} style={{flex: 1, padding: '0.75rem', background: tab==='entry' ? '#1976d2' : '#eee', color: tab==='entry' ? 'white' : '#1976d2', border: 'none', borderRadius: '0.5rem', fontWeight: 600, cursor: 'pointer'}}>Trainingseintrag</button>
                <button onClick={() => setTab('stats')} style={{flex: 1, padding: '0.75rem', background: tab==='stats' ? '#1976d2' : '#eee', color: tab==='stats' ? 'white' : '#1976d2', border: 'none', borderRadius: '0.5rem', fontWeight: 600, cursor: 'pointer'}}>Auswertung</button>
            </div>
            {tab === 'entry' && (
                <>
                    <h2 style={{textAlign: 'center', color: '#1976d2'}}>Workout Tracking</h2>
                    <div style={{marginBottom: '2rem', background: '#f0f4fa', padding: '1rem', borderRadius: '0.5rem'}}>
                        <h3 style={{color: '#1976d2'}}>Trainingsplan für Right Guard (American Football)</h3>
                        <p><strong>3x pro Woche, je max. 2h, Wiedereinstieg nach Sommerpause</strong></p>
                        <ul style={{paddingLeft: '1.2rem'}}>
                            <li><strong>Tag 1: Ganzkörper-Kraft</strong><br />
                                Kniebeugen, Bankdrücken, Rudern, Planks, Farmer's Walk
                            </li>
                            <li><strong>Tag 2: Explosivität & Schnelligkeit</strong><br />
                                Kettlebell Swings, Box Jumps, Medizinballwürfe, Agility Ladder, Core
                            </li>
                            <li><strong>Tag 3: Oberkörper & Mobility</strong><br />
                                Schulterdrücken, Klimmzüge (Assist), Push-Ups, Mobility-Übungen, Stretching
                            </li>
                        </ul>
                        <p style={{fontSize: '0.95rem', color: '#555'}}>
                            <strong>Empfehlung:</strong> Starte mit moderaten Gewichten, Fokus auf Technik und saubere Ausführung. Steigere dich jede Woche leicht.<br />
                            Saisonvorbereitung: Ab Woche 5 Intensität und Gewicht langsam erhöhen, Speed- und Kraftübungen ausbauen.
                        </p>
                    </div>
                    <form
                        onSubmit={(e: FormEvent<HTMLFormElement>) => {
                            e.preventDefault();
                            handleAddWorkout();
                        }}
                        style={{display: 'grid', gap: '1rem', marginBottom: '2rem'}}
                    >
                        <div style={{display: 'grid', gap: '0.5rem'}}>
                            <label htmlFor="exercise" style={{fontWeight: 500}}>Übung</label>
                            <input
                                id="exercise"
                                type="text"
                                placeholder="Exercise"
                                value={exercise}
                                onChange={(e: ChangeEvent<HTMLInputElement>) => setExercise(e.target.value)}
                                required
                                style={{padding: '0.5rem', borderRadius: '0.5rem', border: '1px solid #ccc'}}
                            />
                        </div>
                        <div style={{display: 'grid', gap: '0.5rem'}}>
                            <label htmlFor="weight" style={{fontWeight: 500}}>Gewicht (kg)</label>
                            <input
                                id="weight"
                                type="number"
                                placeholder="Gewicht"
                                value={weight}
                                onChange={(e: ChangeEvent<HTMLInputElement>) => setWeight(e.target.value)}
                                required
                                style={{padding: '0.5rem', borderRadius: '0.5rem', border: '1px solid #ccc'}}
                            />
                        </div>
                        <div style={{display: 'grid', gap: '0.5rem'}}>
                            <label htmlFor="sets" style={{fontWeight: 500}}>Sätze</label>
                            <input
                                id="sets"
                                type="number"
                                placeholder="Sets"
                                value={sets}
                                onChange={(e: ChangeEvent<HTMLInputElement>) => setSets(e.target.value)}
                                required
                                style={{padding: '0.5rem', borderRadius: '0.5rem', border: '1px solid #ccc'}}
                            />
                        </div>
                        <div style={{display: 'grid', gap: '0.5rem'}}>
                            <label htmlFor="reps" style={{fontWeight: 500}}>Wiederholungen</label>
                            <input
                                id="reps"
                                type="number"
                                placeholder="Reps"
                                value={reps}
                                onChange={(e: ChangeEvent<HTMLInputElement>) => setReps(e.target.value)}
                                required
                                style={{padding: '0.5rem', borderRadius: '0.5rem', border: '1px solid #ccc'}}
                            />
                        </div>
                        <div style={{display: 'grid', gap: '0.5rem'}}>
                            <label htmlFor="date" style={{fontWeight: 500}}>Datum</label>
                            <input
                                id="date"
                                type="date"
                                value={date}
                                onChange={(e: ChangeEvent<HTMLInputElement>) => setDate(e.target.value)}
                                required
                                style={{padding: '0.5rem', borderRadius: '0.5rem', border: '1px solid #ccc'}}
                            />
                        </div>
                        <div style={{display: 'grid', gap: '0.5rem'}}>
                            <label htmlFor="notes" style={{fontWeight: 500}}>Notizen</label>
                            <input
                                id="notes"
                                type="text"
                                placeholder="Notizen"
                                value={notes}
                                onChange={(e: ChangeEvent<HTMLInputElement>) => setNotes(e.target.value)}
                                style={{padding: '0.5rem', borderRadius: '0.5rem', border: '1px solid #ccc'}}
                            />
                        </div>
                        <button type="submit" style={{padding: '0.75rem', background: '#1976d2', color: 'white', border: 'none', borderRadius: '0.5rem', fontWeight: 600, cursor: 'pointer'}}>Workout hinzufügen</button>
                    </form>
                    <h3 style={{marginBottom: '1rem', color: '#1976d2'}}>Deine Einträge</h3>
                    <ul style={{listStyle: 'none', padding: 0, marginBottom: '2rem'}}>
                        {workouts.map((workout, index) => (
                            <li key={index} style={{background: '#f5f5f5', marginBottom: '0.5rem', padding: '0.75rem', borderRadius: '0.5rem'}}>
                                <strong>{workout.date}</strong>: {workout.exercise} – {workout.weight} kg, {workout.sets} Sätze x {workout.reps} Wdh. {workout.notes && <>| <em>{workout.notes}</em></>}
                            </li>
                        ))}
                    </ul>
                    <button onClick={exportCSV} style={{padding: '0.75rem', background: '#43a047', color: 'white', border: 'none', borderRadius: '0.5rem', fontWeight: 600, cursor: 'pointer'}}>Export als CSV</button>
                </>
            )}
            {tab === 'stats' && (
                <>
                    <h2 style={{textAlign: 'center', color: '#1976d2'}}>Auswertung</h2>
                    <div style={{background: '#f0f4fa', padding: '1rem', borderRadius: '0.5rem', marginBottom: '2rem'}}>
                        <p><strong>Trainingsanzahl:</strong> {workouts.length}</p>
                        <p><strong>Gesamtgewicht bewegt:</strong> {workouts.reduce((sum, w) => sum + (w.weight * w.sets * w.reps), 0)} kg</p>
                        <p><strong>Durchschnittliches Gewicht pro Satz:</strong> {workouts.length > 0 ? (workouts.reduce((sum, w) => sum + w.weight, 0) / workouts.length).toFixed(2) : 0} kg</p>
                        <p><strong>Letztes Training:</strong> {workouts.length > 0 ? workouts[workouts.length-1].date : '-'}</p>
                    </div>
                    <h3 style={{marginBottom: '1rem', color: '#1976d2'}}>Alle Einträge</h3>
                    <ul style={{listStyle: 'none', padding: 0}}>
                        {workouts.map((workout, index) => (
                            <li key={index} style={{background: '#f5f5f5', marginBottom: '0.5rem', padding: '0.75rem', borderRadius: '0.5rem'}}>
                                <strong>{workout.date}</strong>: {workout.exercise} – {workout.weight} kg, {workout.sets} Sätze x {workout.reps} Wdh. {workout.notes && <>| <em>{workout.notes}</em></>}
                            </li>
                        ))}
                    </ul>
                </>
            )}
        </div>
    );
};

export default TrackingSystem;
